INSERT INTO `template_config` VALUES ('11', 'durc_SearchEntity', 'entity', 'java', 'package ${context.packageName}.entity.${ModelName};\r\n\r\nimport java.util.Date;\r\n\r\nimport org.durcframework.core.expression.annotation.ValueField;\r\nimport org.durcframework.core.support.SearchEasyUI;\r\nimport org.durcframework.core.util.DateUtil;\r\n\r\n\r\npublic class ${context.javaBeanName}Sch extends SearchEasyUI{\r\n\r\n	//根据时间进行搜索的部分\r\n	private Date createdStartSch;\r\n	private Date createdEndSch;\r\n\r\n	@ValueField(column = \"createdTime\", equal = \">=\")\r\n	public Date getCreatedStartSch() {\r\n		return createdStartSch;\r\n	}\r\n\r\n	public void setCreatedStartSch(Date createdStartSch) {\r\n		this.createdStartSch = createdStartSch;\r\n	}\r\n\r\n	@ValueField(column = \"createdTime\", equal = \"<\")\r\n	public Date getCreatedEndSch() {\r\n		if (createdEndSch != null) {\r\n			return DateUtil.getDateAfterDay(createdEndSch, 1);\r\n		}\r\n		return createdEndSch;\r\n	}\r\n\r\n	public void setCreatedEndSch(Date createdEndSch) {\r\n		this.createdEndSch = createdEndSch;\r\n	}\r\n\r\n\r\n#foreach($column in $columns) \r\n    private ${column.javaTypeBox} ${column.columnName}Sch;\r\n#end\r\n\r\n#foreach(${column} in ${columns}) \r\n    public void set${column.javaFieldNameUF}Sch(${column.javaTypeBox} ${column.columnName}Sch){\r\n        this.${column.columnName}Sch = ${column.columnName}Sch;\r\n    }\r\n    \r\n    @ValueField(column = \"${column.columnName}\")\r\n    public ${column.javaTypeBox} get${column.javaFieldNameUF}Sch(){\r\n        return this.${column.columnName}Sch;\r\n    }\r\n\r\n#end\r\n\r\n}', 'admin', 'Sch');
INSERT INTO `template_config` VALUES ('12', 'durc_Mybatis', 'mybatis/mapper', 'xml', '<?xml version=\"1.0\" encoding=\"UTF-8\" ?>\r\n<!DOCTYPE  mapper PUBLIC \"-//mybatis.org//DTD Mapper 3.0//EN\" \"http://mybatis.org/dtd/mybatis-3-mapper.dtd\" >\r\n<mapper namespace=\"${context.packageName}.dao.${ModelName}.${context.javaBeanName}Dao\">\r\n    <resultMap id=\"queryResultMap\" type=\"${context.packageName}.entity.${ModelName}.${context.javaBeanName}\">\r\n        #foreach($column in $columns)\r\n        <result column=\"${column.columnName}\" property=\"${column.javaFieldName}\" jdbcType=\"${column.mybatisJdbcType}\" />\r\n        #end\r\n    </resultMap>\r\n\r\n    <select id=\"find\" parameterType=\"org.durcframework.core.expression.ExpressionQuery\"\r\n		resultMap=\"queryResultMap\">\r\n		SELECT t.*\r\n		FROM ${table.tableName} t\r\n		<include refid=\"expressionBlock.where\" />\r\n		<choose>\r\n			<when test=\"sortname == null\">\r\n				ORDER BY t.${context.pkName} desc\r\n			</when>\r\n			<otherwise>\r\n				ORDER BY ${order}\r\n			</otherwise>\r\n		</choose>\r\n		<if test=\"!isQueryAll\">\r\n			LIMIT\r\n            #{firstResult,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}\r\n		</if>\r\n	</select>\r\n\r\n\r\n    <select id=\"findTotalCount\" parameterType=\"org.durcframework.core.expression.ExpressionQuery\"\r\n		resultType=\"java.lang.Integer\">\r\n		SELECT count(*) FROM ${table.tableName} t\r\n		<include refid=\"expressionBlock.where\" />\r\n    </select>\r\n\r\n    <insert id=\"save\" parameterType=\"${context.packageName}.entity.${ModelName}.${context.javaBeanName}\"\r\n#if(${pkColumn.isIdentity})\r\n    keyProperty=\"${context.javaPkName}\" keyColumn=\"${context.pkName}\" useGeneratedKeys=\"true\"\r\n#end\r\n    >\r\n	INSERT INTO ${table.tableName}\r\n    <trim prefix=\"(\" suffix=\")\" suffixOverrides=\",\">\r\n        \r\n        #foreach($column in $columns) \r\n        <if test=\"${column.javaFieldName} != null\">\r\n            `${column.columnName}`