earchEntitySch);\r\n    }\r\n\r\n    //获取详细信息\r\n	@RequestMapping(\"/get${context.javaBeanName}ById.do\")\r\n    public @ResponseBody ${context.javaBeanName} get${context.javaBeanName}ById(${context.javaBeanName} entity) {\r\n        return this.getService().get(entity.getId());\r\n    }\r\n\r\n 	\r\n\r\n}', 'admin', 'Controller');
INSERT INTO `template_config` VALUES ('20', 'durc_Jsp', 'view/jsp', 'jsp', '<%@ page language=\"java\" contentType=\"text/html; charset=utf-8\"\r\n    pageEncoding=\"utf-8\"%>\r\n<!DOCTYPE html PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\" \"http://www.w3.org/TR/html4/loose.dtd\">\r\n<html>\r\n    <head>\r\n        <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\">\r\n        <title>Insert title here</title>\r\n        <jsp:include page=\"/view/taglib.jsp\"></jsp:include>\r\n    </head>\r\n    <body>\r\n        <div class=\"easyui-layout\" data-options=\"fit:true\">\r\n            <div data-options=\"region:\'north\',split:true,border:false\" style=\"height:80px;\">\r\n                <form id=\"searchForm\">\r\n                    <table>\r\n                        <tr>\r\n                            <td><a id=\"btn_add\" class=\"easyui-linkbutton\" data-options=\"group:\'g1\',iconCls:\'icon-add\'\">添加</a></td>\r\n                            <td><a id=\"btn_dele\" class=\"easyui-linkbutton\" data-options=\"group:\'g1\',iconCls:\'icon-remove\'\">删除</a></td>\r\n                            <td><a onclick=\"grid.datagrid(\'reload\');\" href=\"javascript:void(0);\" class=\"easyui-linkbutton\" data-options=\"iconCls:\'ext-icon-arrow_refresh\'\">刷新</a></td>\r\n                        </tr>\r\n                    </table>\r\n                    <table>\r\n                        <tr>\r\n                            <td>用户名称</td>\r\n                            <td><input name=\"user_nameSch\" class=\"textbox\" style=\"width: 120px; height: 22px;\" /></td>\r\n                            <td>创建时间</td>\r\n                            <td><input name=\"createdStartSch\" class=\"easyui-datebox\" style=\"width: 100px\" />\r\n                                -\r\n                                <input name=\"createdEndSch\" class=\"easyui-datebox\" style=\"width: 100px\" />\r\n                            </td>\r\n                            <td><a href=\"javascript:void(0);\" class=\"easyui-linkbutton\" data-options=\"iconCls:\'ext-icon-zoom\',plain:true\" onclick=\"grid.datagrid(\'load\',fw.serializeObject($(\'#searchForm\')));\">过滤</a> <a href=\"javascript:void(0);\" id=\"reset_searchFilter\" class=\"easyui-linkbutton\" data-options=\"iconCls:\'ext-icon-zoom_out\',plain:true\" onclick=\"$(\'#searchForm input\').val(\'\');grid.datagrid(\'load\',{});\">重置过滤</a></td>\r\n                        </tr>\r\n                    </table>\r\n                </form>\r\n            </div>\r\n            <div data-options=\"region:\'center\',border:false\">\r\n                <table id=\"grid\">\r\n                </table>\r\n            </div>\r\n            <div data-options=\"region:\'east\',split:true,border:false\" style=\"width: 50%; padding: 5px;\">\r\n                <form id=\"form\" name=\"form\" method=\"post\" class=\"form\">\r\n                    <fieldset>\r\n                        <legend> 基本信息--<span id=\"operateMode_title\">添加模式</span> </legend>\r\n                        <table class=\"table\">\r\n                            <tbody>\r\n                                #foreach($column in $columns)\r\n                                #if(${column.isIdentityPk})\r\n                                <tr>\r\n                                    <th>${column.javaFieldName}</th>\r\n                                    <td><input id=\"${column.javaFieldName}\" readonly=\"readonly\" name=\"${column.javaFieldName}\" class=\"textbox\"      style=\"height: 22px;\" /></td>\r\n                                </tr>\r\n                                #end                       \r\n                                #if(!${column.isIdentityPk})    \r\n                                #if(${column.javaType}==\'boolean\')\r\n                                <tr>\r\n                                    <th>${column.javaFieldName}</th>\r\n                                    <td>\r\n                                    <td><input id=\"${column.javaFieldName}\" name=\"${column.javaFieldName}\" class=\"easyui-switchbutton\" checked></td>\r\n                                    </td>\r\n                                </tr>\r\n                                #end        \r\n                                #if(${column.javaType}==\'int\')\r\n                                <tr>\r\n                                    <th>${column.javaFieldName}</th>\r\n                                    <td><input id=\"${column.javaFieldName}\" value=\"0\" name=\"${column.javaFieldName}\" class=\"textbox\"   style=\"height: 22px;\" /></td>\r\n                                </tr>\r\n                                #end  \r\n                                #if(${column.javaType}==\'byte\')\r\n                                <tr>\r\n                                    <th>${column.javaFieldName}</th>\r\n                                    <td><input id=\"${column.javaFieldName}\" value=\"0\" name=\"${column.javaFieldName}\" class=\"textbox\"   style=\"height: 22px;\" /></td>\r\n                                </tr>\r\n                                #end\r\n                                #if(${column.javaType}==\'String\' and  ${column.type}==\'VARCHAR\')\r\n                                <tr>\r\n                                    <th>${column.javaFieldName}</th>\r\n                                    <td><input id=\"${column.javaFieldName}\" name=\"${column.javaFieldName}\" class=\"textbox\"      style=\"height: 22px;\" /></td>\r\n                                </tr>\r\n                                #end     \r\n                                #if(${column.javaType}==\'String\' and ${column.type}==\'longtext\')\r\n                                <tr>\r\n                                    <th>${column.javaFieldName}</th>\r\n                                    <td colspan=\"3\"><textarea id=\"${column.javaFieldName}\" name=\"${column.javaFieldName}\" style=\"width: 98%; margin-bottom: 3px;\"></textarea>\r\n                                        <input type=\"button\" mode=\"0\" class=\"editor\" onclick=\"EditorRender(\'${column.javaFieldName}\',$(this))\" value=\"切换至编辑器\" />\r\n                                    </td>\r\n                                </tr>\r\n                                #end    \r\n                                #if(${column.javaType}==\'Date\')\r\n                                <tr>\r\n                                    <th>${column.javaFieldName}</th>\r\n                                    <td><input id=\"${column.javaFieldName}\" name=\"${column.javaFieldName}\" data-options=\"disabled:true\" class=\"easyui-datetimebox\"  style=\"width: 180px;\" /></td>\r\n                                </tr>\r\n                                #end             \r\n                                #end \r\n                                #end\r\n                                <th>操作</th>\r\n                                <td><a id=\"operateMode_submit\" class=\"easyui-linkbutton\" data-options=\"iconCls:\'icon-save\'\">确定</a> <a id=\"operateMode_reset\" class=\"easyui-linkbutton\" data-options=\"iconCls:\'icon-reload\'\">重置</a></td>\r\n                                </tr>\r\n                            </tbody>\r\n                        </table>\r\n                    </fieldset>\r\n                </form>\r\n            </div>\r\n        </div>\r\n        <script type=\"text/javascript\">\r\n            $(function() {\r\n                var gird;\r\n                grid=$(\'#grid\').datagrid({\r\n                    fit:true,\r\n                    border : false,\r\n                    collapsible : false,\r\n                    rownumbers : true,\r\n                    pagination : true,\r\n                    singleSelect : true,\r\n                    rownumbers : true,\r\n                    url:ctx+\'list${context.javaBeanName}.do\',\r\n                    columns:[[\r\n            		#foreach($column in $columns)  \r\n                       \r\n            			#if($velocityCount != 1),#end{ sortable:true,field:\'${column.javaFieldName}\',title:\'${column.javaFieldName}\'}\r\n            		#end \r\n                    ]],\r\n                    onLoadSuccess : function(data) {\r\n                        $(\'.iconImg\').attr(\'src\', fw.pixel_0);\r\n                    },\r\n                    onClickRow : function(index, row) {\r\n                        $(\"#form\").form(\"load\", row);\r\n                        $(\"#operateMode_title\").html(\"修改模式\");\r\n                    }\r\n                });\r\n                //添加操作\r\n                $(\"#btn_add\").click(function() {\r\n                    $(\"#operateMode_title\").html(\"添加模式\");\r\n                    $(\'#grid\').datagrid(\"unselectAll\");\r\n                    $(\"#${context.javaPkName}\").attr(\"value\", 0);\r\n                    $(\"#form\").form(\"reset\");\r\n                })\r\n            \r\n                //删除操作\r\n                $(\"#btn_dele\").click(function() {\r\n                    var rows = $(\'#grid\').datagrid(\'getSelections\');\r\n                    if (rows <= 0) {\r\n                        $.messager.show({\r\n                            title : message.title.normal,\r\n                            msg : message.grid_select,\r\n                            timeout : message.timeout,\r\n                            showType : message.showType\r\n                        });\r\n                    } else {\r\n                        $.messager.confirm(message.title.askTitle, message.dele_comfirm, function(r) {\r\n                            if (r) {\r\n                                var idValue = $(\"#form input[name=\'${context.javaPkName}\']\").val();\r\n                                $.post(ctx+\"del${context.javaBeanName}.do\", {\r\n                                    ${context.pkName} : idValue\r\n                                }, function(result) {\r\n                                    if (result.success) {\r\n                                        $.messager.show({\r\n                                            title : message.title.normal,\r\n                                            msg : message.dele_success,\r\n                                            timeout : message.timeout,\r\n                                            showType : message.showType\r\n                                        });\r\n                                        grid.datagrid(\'reload\');\r\n                                    } else {\r\n                                        $.messager.show({\r\n                                            title : message.title.normal,\r\n                                            msg : message.dele_fail,\r\n                                            timeout : message.timeout,\r\n                                            showType : message.showType\r\n                                        });\r\n                                    }\r\n                                }, \'json\');\r\n                                $(\"#btn_add\").click();\r\n                            }\r\n                        });\r\n                    }\r\n                })\r\n            \r\n                //确认提交操作\r\n                $(\"#operateMode_submit\").click(function() {\r\n                    var idValue = $(\"#form input[name=\'${context.javaPkName}\']\").val();\r\n                    if ($(\'#form\').form(\'validate\')) {\r\n                        if (idValue > 0) {\r\n                            //修改\r\n                            $.post(ctx+\"update${context.javaBeanName}.do\", fw.serializeObject($(\'form\')), function(result) {\r\n                                if (result.success) {\r\n                                    $.messager.show({\r\n                                        title : message.title.normal,\r\n                                        msg : message.update_success,\r\n                                        timeout : message.timeout,\r\n                                        showType : message.showType\r\n                                    });\r\n            \r\n                                    grid.datagrid(\"reload\");\r\n            \r\n                                    //重新获取form信息内容\r\n                                    $(\"#form\").form(\"load\", ctx+\"get${context.javaBeanName}By${context.javaPKNameUppercase}.do?${context.javaPkName}=\" + idValue);\r\n                                }\r\n                            }, \'json\');\r\n                        } else {\r\n                            if(idValue.trim().length == 0){\r\n                                $.messager.show({\r\n                                    title : message.title.normal,\r\n                                    msg : message.add_button_click,\r\n                                    timeout : message.timeout,\r\n                                    showType : message.showType\r\n                                });\r\n                            }else{\r\n                                //添加\r\n                                $.post(ctx+\"add${context.javaBeanName}.do\", fw.serializeObject($(\'form\')), function(result) {\r\n                                    if (result.success) {\r\n                                        $.messager.show({\r\n                                            title : message.title.normal,\r\n                                            msg : message.add_success,\r\n                                            timeout : message.timeout,\r\n                                            showType : message.showType\r\n                                        });\r\n                                        grid.datagrid(\"reload\");\r\n                                        $(\"#form\").form(\"reset\");\r\n                                    }\r\n                                }, \'json\');\r\n                            }\r\n                        }\r\n                    }\r\n                })\r\n                //重置操作\r\n                $(\"#operateMode_reset\").click(function() {\r\n                    var idValue = $(\"#form input[name=\'${context.javaPkName}\']\").val();\r\n                    if (idValue > 0) {\r\n                        $(\"#form\").form(\"load\",ctx+\"get${context.javaBeanName}By${context.javaPKNameUppercase}.do?${context.javaPkName}=\" + idValue);\r\n                    } else {\r\n                        $(\"#form\").form(\"reset\");\r\n                    }\r\n                })\r\n            });\r\n        </script>\r\n    </body>\r\n</html>', 'admin', '');
INSERT INTO `template_config` VALUES ('21', 'easyui_Jsp', null, null, '<%@page import=\"org.durcframework.autocode.common.AutoCodeContext\"%>\r\n<c:set var=\"base\"\r\n       value=\'<%=request.getScheme()+\"://\"+request.getServerName()+\":\"+request.getServerPort()+request.getContextPath()+\"/\" %>\'/>\r\n\r\n<%@ page language=\"java\" contentType=\"text/html; charset=utf-8\"\r\npageEncoding=\"utf-8\"%>\r\n<!DOCTYPE html PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\" \"http://www.w3.org/TR/html4/loose.dtd\">\r\n<html>\r\n<head>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\">\r\n    <title>${table.tableName}</title>\r\n    <jsp:include page=\"../../../easyui_lib.jsp\"></jsp:include>\r\n    <style type=\"text/css\">\r\n        .fm_lab {\r\n            text-align: right;\r\n            padding: 10px;\r\n        }\r\n    </style>\r\n</head>\r\n<body>\r\n<div id=\"toolbar\">\r\n    <a href=\"#\" class=\"easyui-linkbutton\" iconCls=\"icon-add\" plain=\"true\" onclick=\"crud.add()\">添加</a>\r\n</div>\r\n<table id=\"dg\"></table>\r\n<div id=\"templateTb\">\r\n    #foreach($column in $columns)\r\n        #if(${column.isIdentityPk} or ${column.javaType}==\'int\' or (${column.javaType}==\'String\' and  ${column.type}==\'VARCHAR\'))\r\n            ${column.javaFieldName}:&nbsp;<input id=\"${column.javaFieldName}Sch\" class=\"easyui-textbox\"\r\n                                                 type=\"text\"></input>\r\n        #end\r\n        #if(!${column.isIdentityPk})\r\n            #if(${column.javaType}==\'Date\')\r\n                <tr>\r\n                    <td class=\"fm_lab\">${column.javaFieldName}</td>\r\n                    <td><input id=\"${column.javaFieldName}\" name=\"${column.javaFieldName}\" class=\"easyui-validatebox\"\r\n                               required=\"true\"></td>\r\n                    <td><input name=\"createdStartSch\" class=\"easyui-datebox\" style=\"width: 100px\"/>\r\n                        -\r\n                        <input name=\"createdEndSch\" class=\"easyui-datebox\" style=\"width: 100px\"/>\r\n                    </td>\r\n                </tr>\r\n            #end\r\n        #end\r\n    #end\r\n    <a href=\"#\" class=\"easyui-linkbutton\" iconCls=\"icon-search\" onclick=\"searchTemplate(); return false;\">搜索</a>\r\n</div>\r\n\r\n<div id=\"dlg\" class=\"easyui-dialog\" style=\"width:520px;height:220px;padding:10px 20px\"\r\n     closed=\"true\" modal=\"true\" buttons=\"#dlg-buttons\">\r\n    <form id=\"fm\" method=\"post\">\r\n        <table>\r\n            #foreach($column in $columns)\r\n                #if(${column.isIdentityPk})\r\n                    <tr>\r\n                        <th>${column.javaFieldName}</th>\r\n                        <td><input id=\"${column.javaFieldName}\"  name=\"${column.javaFieldName}\"\r\n                                   class=\"textbox\" style=\"height: 22px;\"/></td>\r\n                    </tr>\r\n                #end\r\n                #if(!${column.isIdentityPk})\r\n                    #if(${column.javaType}==\'boolean\')\r\n                        <tr>\r\n                            <td class=\"fm_lab\">${column.javaFieldName}</td>\r\n                            <td><input id=\"${column.javaFieldName}\" name=\"${column.javaFieldName}\"\r\n                                       class=\"easyui-switchbutton\" checked></td>\r\n                        </tr>\r\n                    #end\r\n                    #if(${column.javaType}==\'int\')\r\n                        <tr>\r\n                            <td class=\"fm_lab\">${column.javaFieldName}</td>\r\n                            <td><input id=\"${column.javaFieldName}\" name=\"${column.javaFieldName}\"\r\n                                       class=\"easyui-validatebox\" required=\"true\"></td>\r\n                        </tr>\r\n                    #end\r\n                    #if(${column.javaType}==\'byte\')\r\n                        <tr>\r\n                            <th>${column.javaFieldName}</th>\r\n                            <td><input id=\"${column.javaFieldName}\" value=\"0\" name=\"${column.javaFieldName}\"\r\n                                       class=\"textbox\" style=\"height: 22px;\"/></td>\r\n                        </tr>\r\n                    #end\r\n                    #if(${column.javaType}==\'String\' and  ${column.type}==\'VARCHAR\')\r\n                        <tr>\r\n                            <td class=\"fm_lab\">${column.javaFieldName}</td>\r\n                            <td><input id=\"${column.javaFieldName}\" name=\"${column.javaFieldName}\"\r\n                                       class=\"easyui-validatebox\" required=\"true\"></td>\r\n                        </tr>\r\n                    #end\r\n                    #if(${column.javaType}==\'String\' and ${column.type}==\'longtext\')\r\n                        <tr>\r\n                            <td class=\"fm_lab\">${column.javaFieldName}</td>\r\n                            <td colspan=\"3\"><textarea id=\"${column.javaFieldName}\" name=\"${column.javaFieldName}\"\r\n                                                      style=\"width: 98%; margin-bottom: 3px;\"></textarea>\r\n                                <input type=\"button\" mode=\"0\" class=\"editor\"\r\n                                       onclick=\"EditorRender(\'${column.javaFieldName}\',$(this))\" value=\"切换至编辑器\"/>\r\n                            </td>\r\n                        </tr>\r\n                    #end\r\n                    #if(${column.javaType}==\'Date\')\r\n                        <tr>\r\n                            <td class=\"fm_lab\">${column.javaFieldName}</td>\r\n                            <td><input id=\"${column.javaFieldName}\" name=\"${column.javaFieldName}\"\r\n                                       data-options=\"disabled:false\" class=\"easyui-datetimebox\" />\r\n                            </td>\r\n                        </tr>\r\n                    #end\r\n                #end\r\n            #end\r\n        </table>\r\n    </form>\r\n</div>\r\n<div id=\"dlg-buttons\">\r\n    <a href=\"#\" class=\"easyui-linkbutton\" iconCls=\"icon-ok\" onclick=\"crud.save(); return false;\">保存</a>\r\n    <a href=\"#\" class=\"easyui-linkbutton\" iconCls=\"icon-cancel\" onclick=\"crud.closeDlg(); return false;\">取消</a>\r\n</div>\r\n\r\n<script type=\"text/javascript\">\r\n    var crud = Crud.create({\r\n        pk: \'username\'\r\n        , listUrl: ctx + \'list${context.javaBeanName}.do\'\r\n        , addUrl: ctx + \'add${context.javaBeanName}.do\'\r\n        , updateUrl: ctx + \'update${context.javaBeanName}.do\'\r\n        , delUrl: ctx + \'del${context.javaBeanName}.do\'\r\n        , dlgId: \'dlg\'\r\n        , formId: \'fm\'\r\n        , gridId: \'dg\'\r\n    });\r\n\r\n    crud.buildGrid([\r\n        #foreach($column in $columns)\r\n            {field:\'${column.javaFieldName}\',title:\'${column.javaFieldName}\'},\r\n        #end\r\n        crud.createOperColumn()\r\n    ]);\r\n\r\n    function searchTemplate() {\r\n        $(\'#dg\').datagrid(\'load\', {\r\n            #foreach($column in $columns)\r\n                #if(!${column.isIdentityPk})\r\n                    #if(${column.javaType}==\'int\')\r\n                        ${column.javaFieldName}Sch: $(\'#${column.javaFieldName}Sch\').val(),\r\n                    #end\r\n\r\n                    #if(${column.javaType}==\'String\' and  ${column.type}==\'VARCHAR\')\r\n                            ${column.javaFieldName}Sch: $(\'#${column.javaFieldName}Sch\').val(),\r\n                    #end\r\n                #end\r\n            #end\r\n        });\r\n    }\r\n\r\n</script>\r\n</body>\r\n</html>', 'admin', null);
